mapper.select:
SELECT m_post.*, m_account.username, m_account.account_img
FROM m_post
         LEFT JOIN m_account ON (m_account.id = m_post.manager_id)
WHERE manager_id = #{manager_id}
LIMIT #{offset}, #{limit};

mapper.update:
UPDATE m_post
SET post_title = #{post_title}
WHERE id = #{id};

UPDATE m_post
SET reply_num = #{reply_num}
WHERE id = #{id};

mapper.select:
SELECT COUNT(*) as num
FROM m_post
WHERE m_post.created_at >= #{created_at_l} AND m_post.created_at <= #{created_at_r}
